SELECT c2.id, c1.address, c1.corpname, c1.number, c1.countyname, c1.countycode, c2.update_time2, c2.v4x10, c2.v4x11, c2.v4x12, c2.v0x1, c2.v0x2, c2.alarm_detail, COALESCE(pp.status,'待处置') status, pp.disposaluser, pp.description, pp.remove_time removeTime from cyyy_dev_info c1 LEFT join cyyy_dev_data c2 on c1.id = c2.device_id LEFT JOIN pollute_police pp ON c2.id = pp.pk_id
and c1.corpname like CONCAT('%', #{corpname},'%')
and c1.countycode = #{region}
and c2.update_time2 >= #{startTime}
and c2.update_time2 <= #{endTime}
and COALESCE(pp."status",'待处置') in
#{item}
and c2.alarm != 0
order by c2.update_time2 LIMIT #{pageSize} OFFSET #{pageNum}

